import React, {useEffect, Suspense} from 'react'
import {connect} from "react-redux";
import {selectCollection, selectCollectionsLoading} from "../../redux/shop/shop.selector";
import CollectionItem from "../../components/collection-item/collection-item.component";
import "./collection.styles.scss";
import {CollectionContainer} from "./collection.styles"
import {fetchCollectionsStart} from "../../redux/shop/shop.actions";
import Spinner from "../../components/spinner/spinner.component";
const CollectionPage = ({isLoading , match, collection}) => {
  console.log(isLoading)
  return (
    <Suspense fallback={Spinner}>
      {/*<div className="title">{title}</div>
       <div className="collection-items">
        {items.map(item => (
          <CollectionItem key={item.id} item={item}/>
        ))} 
        </div> */}
    </Suspense>
  )
}

const mapStateToProps = (state, ownProps) => ({
  collection : selectCollection(ownProps.match.params.collectionId)(state), 
  isLoading : selectCollectionsLoading 
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStart : () => dispatch(fetchCollectionsStart())
})

export default connect(mapStateToProps, mapDispatchToProps)(CollectionPage)
