import React from 'react'
import {connect} from "react-redux";
import {selectCollection} from "../../redux/shop/shop.selector";
import CollectionItem from "../../components/collection-item/collection-item.component";
import "./collection.styles.scss";
import {firestore} from "../../utils/firebase.util";
const CollectionPage = ({match, collection}) => {
  // React.useEffect(() => {
  //   console.log("Iam subcribing");
  //   const ubsubcribeCollectionsPage = firestore.collection("collections").onSnapshot(snapshot => console.log(snapshot));
  //   return () => {
  //     console.log("I am unsubcribe");
  //     ubsubcribeCollectionsPage();
  //   }
  // })
  console.log(collection);
  const {title, items} = collection
  return (
    <div className="collection-page">
      <div className="title">{title}</div>
      <div className="collection-items">
        {items.map(item => (
          <CollectionItem key={item.id} item={item}/>
        ))}
      </div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => ({
  collection : selectCollection(ownProps.match.params.collectionId)(state), 
})

export default connect(mapStateToProps)(CollectionPage)
