import React from 'react'
import {Link} from "react-router-dom";
import {ReactComponent as Logo} from "../../assets/images/crown.svg";
import {auth} from "../../utils/firebase.util";
import CartIcon from "../cart-icon/cart-icon.component";
import CartDropdown from "../cart-dropdown/cart-dropdown.component";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
import {selectCartShow} from "../../redux/cart/cart.selectors";
import {selectCurrentUser, selectUserLoading} from "../../redux/user/user.selectors";
import {withRouter} from "react-router-dom";
// import "./header.styles.scss";
import {HeaderContainer, HeaderContent, LogoContainer, OptionsContainer, OptionDiv, OptionLink} from "./header.styles"

const Header = (props) => { 

  return (
    <HeaderContainer>
      <HeaderContent>
        <LogoContainer to="/">
          <Logo className="logo" />
        </LogoContainer>
        <OptionsContainer>
          <OptionLink to="/shop">SHOP</OptionLink>
          { props.currentUser ?                       
            <OptionLink  onClick={() => auth.signOut()}>SIGN OUT</OptionLink>: 
            props.loading ? "" : 
            <OptionLink className="option" to="/auth">AUTH</OptionLink>
          }   
          { props.location.pathname !== "/checkout" && <CartIcon /> }
        </OptionsContainer>     
      </HeaderContent>
      {props.show && <CartDropdown className="option"/>}
    </HeaderContainer>
  )
}

const mapStateToProps = createStructuredSelector ({
  currentUser : selectCurrentUser,
  show : selectCartShow,
  loading : selectUserLoading
})

export default withRouter(connect(mapStateToProps)(Header))
