import { takeLatest, call } from "redux-saga/effects";
import {
  fetchCollectionsFailure,
  fetchCollectionsSuccess,
} from "./shop.actions";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../utils/firebase.util";
import { shopActionTypes } from "./shop.types";

export function* fetchCollectionsAsyncSaga() {
  try {
    const collectionsRef = firestore.collection("collections");
    const snapshot = yield collectionsRef.get();
    const collectionsMap = yield call(
      convertCollectionsSnapshotToMap,
      snapshot.docs
    );
    yield call(fetchCollectionsSuccess, collectionsMap);
  } catch (error) {
    yield call(fetchCollectionsFailure, error.message);
  }
}

export function* fetchCollectionsStartSaga() {
  yield takeLatest(
    shopActionTypes.FETCH_COLLECTION_START,
    fetchCollectionsAsyncSaga
  );
}
